function formatCurrency(amount){return'Rs '+new Intl.NumberFormat('en-IN',{minimumFractionDigits:2,maximumFractionDigits:2}).format(amount)}function formatDate(date){return new Date(date).toLocaleDateString('en-IN',{year:'numeric',month:'short',day:'numeric'})}function showAlert(message,type='success'){const alertDiv=document.createElement('div');alertDiv.className=`alert alert-${type} alert-dismissible fade show`;alertDiv.innerHTML=`${message}<button type="button" class="btn-close" data-bs-dismiss="alert"></button>`;document.body.insertBefore(alertDiv,document.body.firstChild);setTimeout(()=>alertDiv.remove(),5000)}document.addEventListener('DOMContentLoaded',function(){console.log('TMS Application Loaded')});

